import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { TitleField, ReadOnlyTextField, BodyField, TextAreaField } from '../Fields';
import description from './description';

const BLOCK_WIDTH = 400;                                                           
const BLOCK_HEIGHT = 190;  
const BLOCK_COLOR = '#EEEEEE';

function Hash(props) {
    const [data, setData] = useState("");
    const [hash, setHash] = useState(sha256(""));

    useEffect((data) => {
        setHash(sha256(data));
    }, [hash]);

    return (
        <Container fluid>
            <TitleField titleText={description.title} />
            <BodyField bodyText={description.body} />
            <Container
                style={{ 
                    'width':BLOCK_WIDTH,
                    'height':BLOCK_HEIGHT,
                    'background-color':BLOCK_COLOR,
                }}
            >
                <Form>
                    <TextAreaField 
                        label={'Data:'}
                        callback={(data) => setData(String(data))}
                    />
                    <ReadOnlyTextField 
                        label={'Hash:'} 
                        value={hash} 
                    />    
                </Form>
            </Container>
        </Container>
    );
}

export default Hash;
