import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import SingleBlock from '../SingleBlock';
//import {render} from "react-dom";
import get from '../../descriptionLoader';

let difficulty = 4;        // number of zeros required at front of hash 
let maximumNonce = 500000; // limit the nonce to this so we don't mine too long

class Block extends Component {
    constructor(props) {
        super(props);
        const [title, body] = get("https://cse442-21f.github.io/FP-Blockchain/descriptions/Block.txt"); 
        //const [title, body] = ['title', 'body'];
        this.state = {
            title,
            body
        }
    }

    render() {
        return (
            <Container>
                <h2>
                    {this.state.title}
                </h2>
                <div>
                    {this.state.body.split('\n\n').map(paragraph =>
                        <p>
                            {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                        </p>
                    )}
                </div>
                <div class="row justify-content-center">
                    <div class = "col-md-5" style = {{"background-color":"#50C878"}}>
                        <SingleBlock  />
                    </div>
                </div>
            </Container>
        );
    }
}

export default Block;
