import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import description from './description';
//import {render} from "react-dom";

let difficulty = 4;        // number of zeros required at front of hash
let maximumNonce = 500000; // limit the nonce to this so we don't mine too long

const title = description.title;
const body = description.body;

class Block extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pattern: '0'.repeat(difficulty),
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("172608"),
            //green
            color: "#50C878"
        };
        this.getSequence = this.getSequence.bind(this);
        this.mine = this.mine.bind(this);
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    render() {
        return (
            <Container fluid>
                <h2>
                    {this.state.title}
                </h2>
                <div>
                    {body.split('\n\n').map(paragraph =>
                        <p>
                            {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                        </p>
                    )}
                </div>
                <Container fluid>
                    <Form  onChange={(event) => {
                        event.preventDefault();
                        this.setState({
                            blockNumber: this.blockNumber.value,
                            nonce: this.nonce.value,
                            data: this.data.value,
                            hash : sha256(this.getSequence()),
                            //red
                            color: "#F75D59"
                        });
                    }}>
                        <Form.Group>
                            <Form.Label>
                                Block Number:
                            </Form.Label>
                            <Form.Control
                                type="number"
                                ref={(input) =>
                                    this.blockNumber = input
                                }
                                value = {this.state.blockNumber}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Nonce:
                            </Form.Label>
                            <Form.Control
                                type="number"
                                ref={(input) =>
                                    this.nonce = input
                                }
                                value = {this.state.nonce}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Data:
                            </Form.Label>
                            <Form.Control
                                as="textarea"
                                type="text"
                                placeholder=""
                                ref={(input) =>
                                    this.data = input
                                }
                                value = {this.state.data}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Hash:
                            </Form.Label>
                            <Form.Control
                                type="text"
                                value= {this.state.hash}
                                readOnly
                            />
                        </Form.Group>
                    </Form>
                    <Button onClick={(event) => {
                        event.preventDefault();
                        this.mine(this.state.blockNumber, this.state.data);
                    }}>
                        Press
                    </Button>
                </Container>
            </Container>
        );
    }
}

export default Block;
