import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maximumNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function mine(blockNumber, data, prevHash="", block, setBlock) {
    let sequence = null;
    let nonce = 0;
    let hash = sha256(String(blockNumber + nonce + data + prevHash));
    while (hash.substr(0, difficulty) !== pattern) {
        hash = sha256(String(blockNumber + (nonce++) + data + prevHash));
    }
    setBlock({...block, nonce});
    return;
}

function MineButton({blockNumber, data, prevHash="", block, setBlock}) {
    return (
        <Button 
            onClick={() => mine(blockNumber, data, prevHash, block, setBlock)}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton, mine }
