import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maxNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function MineButton({blockNumber, data, prevHash, callback, block, setBlock}) {
    // Get the hash of the given inputs.
    const hash = (blockNumber, nonce, data, prevHash) => {
        return sha256(blockNumber + nonce + data + prevHash); 
    }

    // Return true if the hash is signed and false otherwise.
    const isSigned = (hash) => hash.substr(0, difficulty) === pattern;

    const [isMining, setIsMining] = useState(false);

    // Recursively search for the nonce that results in a signed hash
    // showing every 10 results.
    const mineDelay = (nonce) => {
        const timer = setTimeout(() => {
            if (isSigned(hash(blockNumber, nonce, data, prevHash))) {
                setBlock({...block, nonce});
                setIsMining(false);
                return;
            } else if (nonce % 10 === 0) {
                setBlock({...block, nonce});
            }

            mineDelay(nonce + 1);
        });
    }

    // Begin mining.
    const handleClick = () => {
        if (!isMining) {
            setIsMining(true);
            mineDelay(0)
        }
    }

    return (
        <Button 
            onClick={() => handleClick()}
        >
            {isMining ? 'Mine!' : 'Mining...'}
        </Button>
    );
};

export { MineButton }
