import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blockNumber: props.blockNumber,
            nonce: 72608,
            data: "",
            hash: sha256("172608"),
            color: "#50c878",
            prevHash: this.props.prevHash,
            blockIndex: 1,
            signed: true
        };
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)
    }

    getHash() {
        return sha256(this.state.blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString() + this.state.prevHash.toString());
    }

    getPattern(hash) {
        return hash.substr(0, difficulty);
    }

    isSigned() {
        return (this.getPattern(this.state.hash) === pattern) ? true : false;
    }

    componentDidMount() {
        let hash = this.getHash();
        this.setState({hash});
        //let prevHash = this.props.prevHash;
        //this.setState({prevHash});
        let nonce = mine(this.state.blockNumber, this.state.data, prevHash);
        this.setState({nonce});
    }

    componentDidUpdate(prevProps, prevState) {
        let hash = this.getHash();
        if (prevState.hash !== hash) {
            this.setState({hash});

            // Pass the updated hash up to the parent blockchain.
            let response = {blockIndex: this.props.blockIndex, hash: this.state.hash};
            this.props.callback(response);
        }

        let prevHash = this.props.prevHash;
        if (prevState.prevHash !== prevHash) {
            this.setState({prevHash});
        }

        let signed = this.isSigned();
        console.log("BlockNum: ", this.state.blockNumber, " PrevHash: ", this.props.prevHash);
        if (prevState.signed !== signed) {
            this.setState({signed});
            this.setState({color: signed ? '#50c878' : '#ff0000'});
        }
    }

    render() {
        return (
            <Container>
                <Container
                    style={{
                        'width':350,
                        'height':500,
                        'background-color':this.state.color
                    }}
                >
                    <Form>
                        <br/>
                        <NumberField
                            label={'Block Number:'}
                            value={this.state.blockNumber}
                            callback={(blockNumber) => this.setState({blockNumber})}
                        />
                        <NumberField
                            label={'Nonce:'}
                            value={this.state.nonce}
                            callback={(nonce) => this.setState({nonce})}
                        />
                        <TextAreaField
                            label={'Data:'}
                            callback={(data) => this.setState({data})}
                        />
                        <ReadOnlyTextField
                            label={'Prev:'}
                            value={this.props.prevHash}
                            callback={(prevHash) => this.setState({prevHash})}
                        />
                        <ReadOnlyTextField
                            label={'Hash:'}
                            value={this.state.hash}
                            callback={(hash) => this.setState({hash})}
                        />
                        <br/>
                        <MineButton
                            blockNumber={this.state.blockNumber}
                            data={this.state.data}
                            prevHash={this.state.prevHash}
                            callback={(nonce) => this.setState({nonce})}
                        />
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default SingleBlock;
