import React, { useState, useEffect } from 'react';
import { Container, Form, Row } from 'react-bootstrap';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton } from '../MineButton';
import MineSettings from '../MineSettings';
import { sha256 } from 'js-sha256';
//import Switch from '../Switch';
import { SwitchButton } from '../Switch';
import DefaultBlocks from '../Blockchain/DefaultBlocks';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

const defaultBlock = DefaultBlocks.block1;

function Block() {
    const [block, setBlock] = useState(defaultBlock);
    const [isSwitchOn, setIsSwitchOn] = useState(false);

    const {blockNumber, nonce, data} = block;

    // Clear the initially set prevHash.
    useEffect(() => {
        setBlock({...block, prevHash:""});
    }, []);

    const hash = () => sha256(blockNumber, nonce, data);
    const isSigned = () => hash().substr(0, difficulty) === pattern;

    return (
        <Container
            style={{
                'width':350,
                'height':525,
                'background-color':isSigned() ? GREEN : RED
            }}
        >
            <Form>
                <br/>
                <NumberField
                    label={'Block Number:'}
                    value={blockNumber}
                    callback={(blockNumber) => setBlock({...block, blockNumber})}
                />
                <NumberField
                    label={'Nonce:'}
                    value={nonce}
                    callback={(nonce) => setBlock({...block, nonce})}
                />
                <TextAreaField
                    label={'Data:'}
                    callback={(data) => setBlock({...block, data})}
                />
                <ReadOnlyTextField
                    label={'Hash:'}
                    value={hash()}
                />
                <br/>
                <Row className="g-2">
                    <MineButton
                        blockNumber={blockNumber}
                        data={data}
                        block={block}
                        setBlock={setBlock}
                        isSwitchOn = {isSwitchOn}
                    />
                    <SwitchButton
                        isSwitchOn = {isSwitchOn}
                        setIsSwitchOn={setIsSwitchOn}
                    />
                </Row>

            </Form>
        </Container>
    );
}

export default Block;
