import React, { Component } from 'react';
import { Container } from 'react-bootstrap';
import Blockchain from './Blockchain'
import { TitleField, SecondTitleField, BodyField } from '../Fields';
import description from './description';

const title = description.title;
const body = description.body;

const RED = '#CD0000';
const GREEN = '#008000';

class Distributed extends Component {
    constructor(props) {
        super(props);
        this.state = {
            lastHashes: ["", "", ""],
        }
    }

    handleUpdate(response) {
        let lastHashes = this.state.lastHashes;
        let index = response.peer;
        let lastHash = response.lastHash;
        lastHashes[index] = lastHash;

        // Prevent recursion
        if (this.state.lastHashes !== lastHashes) {
            this.setState({lastHashes});
        }
    }

    getConsensus() {

    }

    componentDidUpdate() {
        let consensus = getConsensus();
    }

    render() {
        return (
            <Container fluid>
                <TitleField titleText={title} />
                <BodyField bodyText={body} />
                <div 
                    style={{
                        'background-color':GREEN
                    }}
                >
                    <SecondTitleField titleText={'Peer A'} /> 
                    <Blockchain peer={0} callback={(response) => this.handleUpdate(response)} />
                </div>
                <br />
                <div 
                    style={{
                        'background-color':RED
                    }}
                >
                    <SecondTitleField titleText={'Peer B'} callback={(response) => this.handleUpdate(response)} />
                    <Blockchain peer={1} callback={(response) => this.handleUpdate(response)} />
                </div>
                <br />
                <div 
                    style={{
                        'background-color':GREEN
                    }}
                >
                    <SecondTitleField titleText={'Peer C'} callback={(response) => this.handleUpdate(response)} />
                    <Blockchain peer={2} callback={(response) => this.handleUpdate(response)} />
                </div>
                <br />
            </Container>
        );
    }
}

export default Distributed;
