import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from './Fields';
import MineButton from './MineButton';
import get from '../descriptionLoader';

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("1" + "72608" + ""),
            color: "#50C878",
            signed: true
        };
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)        
    }

    getHash() {
        return sha256(this.state.blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString());
    }

    isSigned() {
        if (this.state.hash.substr(0, 4) === "0000") {
            return true;
        } else {
            return false;
        }
    }

    componentDidUpdate(prevProps, prevState) {
        let hash = this.getHash();
        if (prevState.hash !== hash) {
            this.setState({hash});
        }

        let signed = this.isSigned();
        if (prevState.signed != signed) {
            this.setState({signed});
            console.log("isSigned: ", signed);
        }
    }

    render() {
        return (
            <Container fluid>
                <Form>
                    <br/>
                    <NumberField 
                        label={'Block Number:'} 
                        value={this.state.blockNumber} 
                        callback={(blockNumber) => this.setState({blockNumber})}
                    />
                    <NumberField 
                        label={'Nonce:'} 
                        value={this.state.nonce} 
                        callback={(nonce) => this.setState({nonce})} 
                    />
                    <TextAreaField 
                        label={'Data:'} 
                        callback={(data) => this.setState({data})}
                    />
                    <ReadOnlyTextField 
                        label={'Hash:'}
                        value={this.state.hash}
                    />
                    <br/>
                    <MineButton 
                        blockNumber={this.state.blockNumber} 
                        data={this.state.data} 
                        callback={(nonce) => this.updateNonce(nonce)}
                    />
                    <p></p>
                </Form>
            </Container>
        );
    }
}

export default SingleBlock;
