import { Form } from 'react-bootstrap';

function SwitchButton({isSwitchOn, setIsSwitchOn}) {
    const onSwitchAction = () => {
        setIsSwitchOn(!isSwitchOn);
      };

    return (
        <Form>
        <Form.Check
            style={{
                'color': '#277da1'
            }}
            onChange={() => onSwitchAction()}
            type="switch"
            id="custom-switch"
            style={{ color: "white", fontSize: "16pt"}}
            label="Visualize!" 
        />
        </Form>
    );
};

export { SwitchButton }
