import * as $ from 'jquery';

// Get the text from url and return as a title and body.
function get(url) {
    const content = (() => {
        var content = null;
        $.ajax({
            'async': false,
            'url': url,
            'dataType': "text",
            'success': (data) => {
                content = data;
            }
        });
        return content;
    })();
    return split(content);
}

// Split the text on the first newline character.
function split(text) {
    const index = text.indexOf('\n\n');
    return [text.slice(0, index), text.slice(index)];
}

export default get;
