import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maxNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function* nonceIter(nonce) {
    yield nonce;
    nonce++;
}

function MineButton({blockNumber, data, prevHash, callback, block, setBlock}) {
    const hash = (blockNumber, nonce, data, prevHash) => {
        return sha256(blockNumber + nonce + data + prevHash); 
    }

    const isSigned = (hash) => hash.substr(0, difficulty) === pattern;

    const setNonce = (nonce) => {
        if (isSigned(hash(blockNumber, nonce, data, prevHash))) {
            setBlock({...block, nonce});
            return;
        } else {
            const timer = setTimeout(() => {
                if (nonce % 10 === 0) {
                    setBlock({...block, nonce});
                }
                setNonce(nonce + 1);
            });
        }
    }

    const handleClick = () => {
        setNonce(0)
    }

    return (
        <Button 
            onClick={() => handleClick()}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton }
