import Hash from './components/Hash/Hash';
import Block from './components/Block/Block';
import Blockchain from './components/Blockchain/Blockchain';
import Distributed from './components/Distributed/Distributed';
import Tokens from "./components/Tokens/Tokens";
import Coinbase from "./components/Coinbase/Coinbase";
import React from 'react';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.handleColor = this.handleColor.bind(this);
    //start green
    this.state = {color: "#50C878"};
  }

  handleColor(colorVal){
    this.setState({color: colorVal})
  }

  render() {
    return (
      <div className="App">
          <div className="Hash">
              <Hash />
          </div>
          <div className="Block" class = "col-sm-3" 
              Block getColor = {this.handleColor}
              style = {{"background-color":this.state.color}}>
              <Block />
          </div>
          <div className="Blockchain">
              <Blockchain />
          </div>
          <div className="Distributed">
              <Distributed />
          </div>
          <div className="Tokens">
              <Tokens />
          </div>
          <div className="Coinbase">
              <Coinbase />
          </div>

      </div>
    );
  }
}

export default App;
