import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import {render} from "react-dom";
//import get from '../../descriptionLoader';

let difficulty = 4;        // number of zeros required at front of hash
let maximumNonce = 500000; // limit the nonce to this so we don't mine too long

class Block extends Component {
    constructor(props) {
        super(props);
        const [title, body] = ["Block", "Blocky stuff"];
        this.state = {
            pattern: '0'.repeat(difficulty),
            blockNumber: 1,
            nonce: 0,
            data: "",
            hash: "",
            sequence: "", title,
            body,
        };
        this.getSequence = this.getSequence.bind(this);
        this.mine = this.mine.bind(this);
    }

    getSequence() {
        return (this.nonce.value.toString() + this.blockNumber.value.toString() + this.data.value.toString());
    }

    mine() {
        let data = this.getSequence();
        let hashAndNonce = {};
        for (let x = 0; x <= maximumNonce; x++) {
            let nonceString = x.toString();
            let newData = data + nonceString;
            hashAndNonce[0] = (sha256(newData));
            if (hashAndNonce[0].substr(0, difficulty) === this.state.pattern) {
                hashAndNonce[1] = x;
                break;
            }
        }
        this.setState({hash : hashAndNonce[0],
                       nonce: hashAndNonce[1]});
    }

    render() {
        return (
            <Container>
                <h2>
                    {this.state.title}
                </h2>
                <div>
                    {this.state.body.split('\n\n').map(paragraph =>
                        <p>
                            {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                        </p>
                    )}
                </div>
                <Container>
                    <Form  onChange={(event) => {
                        event.preventDefault();
                        this.setState({
                            nonce : this.nonce.value,
                            blockNumber : this.blockNumber.value,
                            data : this.data.value,
                            hash : sha256(this.getSequence().toString())})
                            sequence: this.getSequence()
                    }}

                    // onClick={(event) => {
                    //     event.preventDefault();
                    //     this.setState({
                    //         sequence: mineBlock(this.data.value)})}
                    >
                        <Form.Group>
                            <Form.Label>
                                Block Number: 
                            </Form.Label>
                            <Form.Control 
                                as="textarea"
                                type="text"
                                placeholder='1' 
                                ref={(blockNumberInput) =>
                                    this.blockNumber = blockNumberInput
                                }
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Nonce: 
                            </Form.Label>
                            <Form.Control 
                                as="textarea"
                                type="number"
                                placeholder="1234" 
                                ref={(nonceInput) =>
                                    this.nonce = nonceInput
                                }
                                value = {this.state.nonce}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Data: 
                            </Form.Label>
                            <Form.Control 
                                as="textarea"
                                type="text"
                                placeholder="" 
                                ref={(input) =>
                                    this.data = input
                                }
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Hash:  
                            </Form.Label>
                            <Form.Control 
                                type="text" 
                                value= {this.state.hash}
                                plaintext readOnly
                            />
                        </Form.Group>
                    </Form>
                    <Button onClick={(event) => {
                        event.preventDefault();
                        this.mine();
                    }}>
                        Press
                    </Button>
                </Container>
            </Container>
        );
    }
}

export default Block;
